definition.script:
node {
     def build_id = env.BUILD_ID
     if(!PARAMETER_PARENT_JOB_ID.equals('')) {
         build_id = PARAMETER_PARENT_JOB_ID
     }
     print(build_id)
     def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
     print(dockerfile_dir)
     dir(dockerfile_dir) {
         stage('OS Build') {
             def os_to_build = PARAMETER_PLATFORM_TARGETS.split(',')
             def parallelBranches = [:]
             for(int i = 0; i < os_to_build.size(); i++) {
                def os = os_to_build[i]
                def build_cmd = 'python build_os.py -p ' + os + ' -b ' + build_id 
                parallelBranches["${os}"] = {
                    sh build_cmd 
                }
             }
             if (parallelBranches.size() != 0) {
                 parallel(parallelBranches)
             }
         }
     }
}
