definition.script:
node {
     def ub16_dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO + '/Ubuntu_16'

     dir(ub16_dockerfile_dir) {
         stage('OS Build') {
             sh 'docker build -t centos_7:${BUILD_ID} -f Dockerfile.centos .'
         }
         stage('iRODS Build') {
             sh 'docker build -t cen7_irods_build:${BUILD_ID} --build-arg base_image=centos_7:${BUILD_ID} -f Dockerfile.build_irods .'
         }
         stage('save iRODS Build') {
             sh 'docker run --rm -v $JENKINS_OUTPUT/${JOB_NAME}/${BUILD_NUMBER}:/jenkins_output cen7_irods_build:${BUILD_ID}'
         }
         stage('iRODS Install') {
             sh 'docker build -t cen7_irods_install:${BUILD_ID} --build-arg base_image=centos_7:${BUILD_ID} -f Dockerfile.install_and_test .'
         }
         stage('run Tests') {
             sh 'python run_tests_in_parallel.py --image_name cen7_irods_install:${BUILD_ID} --jenkins_output $JENKINS_OUTPUT/${JOB_NAME}/${BUILD_NUMBER} --test_name_prefix ${BUILD_TAG} --database_type postgres'
         }
     }
}
